import React, {useCallback, useContext, useEffect} from 'react';
import {SubmitCheckEmptyContext, TendToSubmitContext} from "../../check&fill/context/submitCheckEmptyContext";

const Single = ({title, options, index}) => {
	// 将用户选择的答案存到sessionStorage
	sessionStorage.setItem(`${title}`, JSON.stringify({answer: "", questionType: 'single', index}));
	const singleChoose = useCallback(e => {
		sessionStorage.setItem(`${title}`, JSON.stringify({answer: e.currentTarget.id, questionType: 'single', index}));
	}, [title]);


	// 检查是否完成填写
	const checkCompleted = useContext(SubmitCheckEmptyContext);
	const tendToSubmit = useContext(TendToSubmitContext);
	useEffect(() => {
		if (tendToSubmit.current) {
			checkCompleted(title, 'single');
		}
	}, [tendToSubmit.current])

	return (
		<fieldset>
			<legend><h3>{title}</h3></legend>
			{options.map(option => (
				<div key={option.id} id={option.id} onClick={singleChoose}>
					<input type={"radio"} name={title} id={`${option.id}_radio_${option.content}`} value={option.content}></input>
					<label htmlFor={`${option.id}_radio_${option.content}`}>{option.content}</label>
				</div>
			))}
		</fieldset>
	);
};

export default Single;
