import React, {useCallback, useContext, useState} from 'react';
import {RenderDraftsContext} from "./context/drafts";
import {useNavigate} from "react-router-dom";
import {getQueDetails} from "../../utils/getUserSurveys";

const RenderDrafts = () => {
	// 鼠标移入移出控制按钮显示
	const [hoverId, setHoverId] = useState(null);
	const handleMouseEnter = useCallback(e => {
		setHoverId(e.target.id);
	}, [hoverId]);
	const handleMouseLeave = useCallback(e => {
		setHoverId(null);
	}, [hoverId]);


	// element参考信息
	const drafts = useContext(RenderDraftsContext);
	const elements = drafts.map(draft => {
		return {
			title: draft.title,
			description: draft.description,
			id: draft.id
		}
	});
	// 编辑跳转
	// const handleEditClick = useCallback(e => {
	// 	e.preventDefault();
	// 	const data = drafts.filter(draft => draft.id === e.currentTarget.id)[0];
	// 	console.log(data);
	// 	sessionStorage.setItem('surveyTitle', data.title);
	// 	sessionStorage.setItem('surveyDescription', data.description);
	// 	data.questions.forEach(question => {
	// 		getQueDetails(question).then(r => {
	// 			sessionStorage.setItem(`${r.title}`, JSON.stringify(r));
	// 		});
	// 	})
	// }, [drafts])
	async function handleEditClick(e) {
		e.preventDefault();
		// 采用编程式导航
		const navigate = useNavigate()
		// 将问卷相关信息存入sessionStorage
		const data = drafts.filter(draft => draft.id === e.currentTarget.id)[0];
		sessionStorage.setItem('surveyTitle', data.title);
		sessionStorage.setItem('surveyDescription', data.description);
		// 等待所有题目信息存入sessionStorage
		await Promise.all(data.questions.map(question => {
			getQueDetails(question).then(r => {
				sessionStorage.setItem(`${r.title}`, JSON.stringify(r));
			});
		}))
		// 跳转到publish
		navigate('../publish')
	}


	console.log(drafts);
	console.log(elements);
	const elementsForRender = elements.map(element => (
		<fieldset key={element.id} id={element.id} onMouseEnter={handleMouseEnter} onMouseLeave={handleMouseLeave}>
			<div>
				<div>{element.title}</div>
				<div>{element.description}</div>
			</div>
			<hr/>
			{hoverId === element.id && (
				<div>
					<button onClick={handleEditClick} id={element.id}>
						{'编辑'}
						{/*<Link to="../publish" className={link.link}>{'编辑'}</Link>*/}
					</button>
					<button>{'预览'}</button>
					<button>{'删除'}</button>
					<button>{'发布'}</button>
				</div>
			)}
		</fieldset>
	))


	return (
		<div>
			{elementsForRender}
		</div>
	);
};

export default RenderDrafts;
