import React, {useCallback, useContext, useEffect, useState} from 'react';
import {SubmitCheckEmptyContext, TendToSubmitContext} from "../../check&fill/context/submitCheckEmptyContext";

const Slider = ({title, max, step, index}) => {
	// 将用户输入的答案存到sessionStorage
	useEffect(() => {
		// 初始化sessionStorage
		sessionStorage.setItem(`${title}`, JSON.stringify({answer: 0, questionType: 'slider', index}));
	}, [title])
	// 分数变化
	const [rate, setRate] = useState(-step);
	const handleRateChange = useCallback(e => {
		const newRate = e.target.value;
		setRate(newRate);
	}, [title])
	// 分数存储
	useEffect(() => {
		sessionStorage.setItem(`${title}`, JSON.stringify({answer: rate, questionType: 'slider', index}));
	}, [rate]);


	// 检查是否完成填写
	const checkCompleted = useContext(SubmitCheckEmptyContext);
	const tendToSubmit = useContext(TendToSubmitContext);
	useEffect(() => {
		const check = () => {
			if (tendToSubmit.current) {
				checkCompleted(title, 'slider');
			}
		};
		window.addEventListener('submit-check', check);

		return () => window.removeEventListener('submit-check', check);
	}, [tendToSubmit, title]);


	return (
		<fieldset>
			<legend><h3>{title}</h3></legend>
			<input type="range" onChange={handleRateChange} id={title} value={rate} min={0} max={max} step={step}/>
			<label htmlFor={title}>{rate}</label>
		</fieldset>
	);
};

export default Slider;
