import {useLoginUserMutation, useRegisterUserMutation, useFindUserQuery, userApi} from "./apis";


export function useLogin() {
}

// 注册---触发器, 结果集
const [handleRegister, result] = useRegisterUserMutation();
// 注册处理器
// 发送请求到服务器的过程为异步, 需要时间传输数据并等待服务器处理, 若不使用异步, 代码会不等待请求完成直接继续执行, 导致无法正确获取服务器的返回结果
export async function register(name, pwd) {
	const data = {
		attributes: {
			username: name,
			pwd
		}
	}
	try {
		// 通过 unwrap 获取原始响应
		const resp = await handleRegister(data).unwrap();
		console.log('注册成功\n', resp);
		return true;
	} catch (err) {
		console.log('注册失败: ', err);
		return false;
	}
}
