import React, {useEffect, useState} from 'react';
import {useNavigate, useParams} from "react-router-dom";
import {checkSurvey} from "../../utils/checkSurvey";
import {RenderFillContext} from "../../components/check&fill/context/renderFillContext";
import RenderFillSurvey from "../../components/check&fill/renderFillSurvey";


// 检查 surveyId 是否合法
const CheckSurvey = () => {
	const {surveyId} = useParams();
	const navigate = useNavigate();
	const [checkResult, setCheckResult] = useState({
		isValid: false, // 问卷是否有效
		surveyData: null, // 问卷详情, title, description, questions, endCollection, userId
		error: {} // 无效原因 code, message
	});
	const currentUser = localStorage.getItem('token');
	const [isChecking, setIsChecking] = useState(true);

	// 检查 surveyId 是否合法
	useEffect(() => {
		if (surveyId) {
			checkSurvey(surveyId).then(r => setCheckResult(r));
			setIsChecking(false);
		}
	}, [surveyId])


	return (
		<div>
			{isChecking ? <h1>{'让我来大调查一下你要填的问卷...'}</h1> : (
				<div>
					<RenderFillContext.Provider value={checkResult}>
						{checkResult.isValid ?
							(currentUser !== checkResult.surveyData.userId ? <RenderFillSurvey surveyId={surveyId}/> :
								<div>{'这不是你该来的地方...'}</div>)
							: <div>{'不合法: '}{checkResult.error.message}</div>}
					</RenderFillContext.Provider>
				</div>)}
		</div>
	);
};

export default CheckSurvey;
