import React, {useCallback, useContext, useEffect, useState} from 'react';
import {RenderFillContext} from "./context/renderFillContext";
import {getQueDetails} from "../../utils/getUserSurveys";
import {quesDisplay} from "./quesDisplay/quesDisplay";
import {useNavigate} from "react-router-dom";
import {SubmitCheckEmptyContext} from "./context/submitCheckEmptyContext";

const RenderFillSurvey = ({surveyId}) => {
	const surveyDetails = useContext(RenderFillContext).surveyData;
	const navigate = useNavigate();

	// 获取问卷对应questionsDetail
	const [questions, setQuestions] = useState([]);
	useEffect(() => {
		setQuestions([]);
		const getQues = async () => {
			// 创建所有请求的Promise数组
			const getQuesPromise = surveyDetails.questions.map(question => {
				return getQueDetails(question);
			});
			// 等待所有promise完成, 获取结果数组
			const results = await Promise.all(getQuesPromise);
			// 一次性更新questions
			const sortedQues = results.sort((a, b) => a.index - b.index);
			setQuestions(sortedQues);
		}
		getQues();
	}, [surveyDetails]);

	// 监控questions变化
	useEffect(() => {
		console.log('questions:----', questions);
	}, [questions]);

	// 获取需要渲染的问题组件
	const elements = quesDisplay(questions);


	// 取消填写问卷, 返回首页, 清空填写问卷时产生的 sessionStorage
	const handleCancel = useCallback(e => {
		e.preventDefault();
		sessionStorage.clear();
		alert('填写的信息将会消失');
		navigate('../../published');
	}, [surveyDetails]);


	// 检查是否全部填写
	const [allCompleted, setAllCompleted] = useState(true);
	/*传递给每个问题组件的检查函数, 当存在任意一个组件未传递答案时, 设置allCompleted为false
	* @accept`title` 问题标题
	* @accept`type` 问题类型*/
	const checkCompleted = useCallback((title, type) => {
		const data = JSON.parse(sessionStorage.getItem(title));
		switch (type) {
			case 'text':
			case 'location':
			case 'single':
			case 'checkbox': {
				if (data.answer.length === 0) {
					alert(`${title} 还没填就想交卷?`);
					setAllCompleted(false);
				}
				break;
			}
			case 'slider':
			case 'rating':{
				if(data.answer < 0){
					alert(`${title} 不许打负分!`);
				}
			}
			default:{
				setAllCompleted(true);
			}
		}
	}, [surveyDetails]);
	// 提交填写的问卷, 返回involved, 清空填写问卷产生的 sessionStorage
	const submit = useCallback(e => {
		e.preventDefault();
	}, [surveyDetails]);
	const handleSubmit = useCallback(async (e) => {


		navigate('../../involved');
	}, [surveyDetails]);


	return (
		<fieldset>
			<h1>{surveyDetails.title}</h1>
			<h2>{surveyDetails.description}</h2>
			<hr/>
			{/*<SubmitCheckEmptyContext.Provider value={}>*/}
				{elements}
			{/*</SubmitCheckEmptyContext.Provider>*/}
			<button onClick={handleCancel}>{'取消填写'}</button>
			<button onClick={submit}>{'提交'}</button>
		</fieldset>
	);
};

export default RenderFillSurvey;
