import React, {useCallback, useEffect, useState} from 'react';
import QuesList from "../components/editSurvey/quesList";
import AddQue from "../components/editSurvey/addQue";
// import {QueContext} from "../components/editSurvey/context/typeContext";
import {GetTitleContext} from "../components/editSurvey/context/getTitleContext";

const Publish = () => {
	// 监测是否正在添加某个问题
	const [adding, setAdding] = useState(false);
	// 是否添加完毕
	const [addComplete, setAddComplete] = useState(false);
	//  问题列表
	const [ques, setQues] = useState([]);
	// const queType = useContext(QueContext);
	// 问题标题
	const [queTitle, setQueTitle] = useState('');
	const handleTitleChange = useCallback(e => {
		const newTitle = e.target.value;
		setQueTitle(newTitle);
		// 将标题存储到 sessionStorage
		sessionStorage.setItem('surveyTitle', newTitle);
	}, []);
	// 问题描述
	const [queDescr, setQueDescr] = useState('');
	const handleDescrChange = useCallback(e => {
		const newDescr = e.target.value;
		setQueDescr(newDescr);
		// 将描述存储到 sessionStorage
		sessionStorage.setItem('surveyDescription', newDescr);
	})

	// 添加问题
	const handleAddQue = useCallback((e) => {
		e.preventDefault();
		setAdding(true);
		setAddComplete(false);
	}, [adding, addComplete]);
	// 确认添加
	const handleCompleteQue = e => {
		e.preventDefault()
		// console.log(queType);
		// setQues([...ques, {id: Date.now(), type: ''}]);
		setAdding(false);
		setAddComplete(true);
	};

	// 获得问题 title 用于定位 sessionStorage 中的存储
	function getTitle(title) {
		console.log(title);
		setQues([...ques, {title, id: Date.now()}]);
	}

	// 取消添加
	const handleCancel = useCallback(e => {
		e.preventDefault();
		setAdding(false);
		setAddComplete(false);
	}, [adding, addComplete])

	useEffect(() => {
		console.log('publish---------', ques);
	}, [queTitle, queDescr, ques]);

	return (
		<div>
			<form action="">
				<input onChange={handleTitleChange} type="text" placeholder={'请输入问卷标题'}/>
				<input onChange={handleDescrChange} type="text" placeholder={'请输入问卷描述'}/>
				<QuesList ques={ques}/>
				<div>
					{!adding && <button onClick={handleAddQue}>添加问题</button>}
					{adding && (
						<>
							{/* 使用context传递获取标题的函数 */}
							<GetTitleContext.Provider value={addComplete ? getTitle : null}>
								<AddQue/>
								<button onClick={handleCancel}>取消</button>
								<button onClick={handleCompleteQue}>确认添加</button>
							</GetTitleContext.Provider>
						</>
					)}
				</div>
			</form>
		</div>
	);
};

export default Publish;
