import React, {useCallback, useContext, useEffect, useState} from 'react';
import {RenderFillContext} from "./context/renderFillContext";
import {getQueDetails} from "../../utils/getUserSurveys";
import {quesDisplay} from "./quesDisplay/quesDisplay";
import {useNavigate} from "react-router-dom";

const RenderFillSurvey = () => {
	const surveyDetails = useContext(RenderFillContext).surveyData;
	const navigate = useNavigate();

	// 获取问卷对应questionsDetail
	const [questions, setQuestions] = useState([]);
	useEffect(() => {
		setQuestions([]);
		const getQues = async () => {
			// 创建所有请求的Promise数组
			const getQuesPromise = surveyDetails.questions.map(question => {
				return getQueDetails(question);
			});
			// 等待所有promise完成, 获取结果数组
			const results = await Promise.all(getQuesPromise);
			// 一次性更新questions
			const sortedQues = results.sort((a, b) => a.index - b.index);
			setQuestions(sortedQues);
		}
		getQues();
	}, [surveyDetails]);

	// 监控questions变化
	useEffect(() => {
		console.log('questions:----', questions);
	}, [questions]);

	// 获取需要渲染的问题组件
	const elements = quesDisplay(questions);


	// 取消填写问卷, 返回首页, 清空填写问卷时产生的 sessionStorage
	const handleCancel = useCallback(e => {
		e.preventDefault();
		sessionStorage.clear();
		alert('填写的信息将会消失');
		navigate('../../published');
	}, [surveyDetails]);


	// 提交填写的问卷, 返回involved, 清空填写问卷产生的 sessionStorage
	const handleSubmit = useCallback(async (e) => {
		e.preventDefault();


		navigate('../../involved');
	}, [surveyDetails]);


	return (
		<fieldset>
			<h1>{surveyDetails.title}</h1>
			<h2>{surveyDetails.description}</h2>
			<hr/>
			{elements}
			<button onClick={handleCancel}>{'取消填写'}</button>
			<button onClick={handleSubmit}>{'提交'}</button>
		</fieldset>
	);
};

export default RenderFillSurvey;
