import React, {createContext, useCallback, useContext, useReducer, useState} from 'react';
import {QueContext, QueDispatchContext} from "./typeContext/typeContext";


function queReducer(que, action) {
	switch (action.type){
		case 'single':{

		}
		case 'checkbox':{

		}
		case 'text':{

		}
		case 'rating':{

		}
		case 'slider':{

		}
		case 'select':{

		}
		case 'location':{

		}
		default:{
			throw new Error('Unknown que type');
		}
	}
}

const initialQue = 'single';
let type = 'single';


const AddQue = () => {
	const [que, dispatch] = useReducer(queReducer, initialQue);

	
	const handleCheckType = useCallback(e => {
		// console.log(e);
		// console.log(e.target.value)
		type = e.target.value;
		dispatch({
			type: e.target.value,
		});
	}, []);



	return (
		<div>
			<select onChange={handleCheckType} name="type" id="">
				<option value="single">单选</option>
				<option value="checkbox">多选</option>
				<option value="text">填空</option>
				<option value="rating">评分</option>
				{/* 拖动滑动条选择数值 */}
				<option value="slider">滑块</option>
				{/* 选项较多时, 如省份选择 */}
				<option value="select">下拉框</option>
				<option value="location">定位</option>
			</select>
			<QueContext value={que}>
				<QueDispatchContext value={dispatch}>

				</QueDispatchContext>
			</QueContext>
		</div>
	);
};

export default AddQue;
export {type};
