import React, {useCallback, useContext, useState} from 'react';
import {RenderDraftsContext} from "./context/drafts";
import draft from "../../pages/draft";

const RenderDrafts = () => {
	// 鼠标移入移出控制按钮显示
	const [isHover, setIsHover] = useState(false);
	const handleMouseEnter = useCallback(e => {
		setIsHover(true);
	}, [isHover]);
	const handleMouseLeave = useCallback(e => {
		setIsHover(false);
	}, [isHover]);


	const drafts = useContext(RenderDraftsContext);
	const elements = drafts.map(draft => {
		return {
			title: draft.title,
			description: draft.description,
			id: draft.id
		}
	});
	console.log(drafts);
	console.log(elements);
	const elementsForRender = elements.map(element => (
		<fieldset key={element.id} onMouseEnter={handleMouseEnter} onMouseLeave={handleMouseLeave}>
			<div>
				<div>{element.title}</div>
				<div>{element.description}</div>
			</div>
			<hr/>
			{isHover && (
				<div>
					<button>{'编辑'}</button>
					<button>{'预览'}</button>
					<button>{'删除'}</button>
					<button>{'发布'}</button>
				</div>
			)}
		</fieldset>
	))


	return (
		<div>
			{elementsForRender}
		</div>
	);
};

export default RenderDrafts;
