import React, {useCallback, useReducer} from 'react';
import {renderQue} from "./renderQue";
import select from '../../style/select/select.module.css'


// 根据当前选项的值返回类型
function queReducer(que, action) {
	switch (action.type) {
		case 'single':
		case 'checkbox':
		case 'text':
		case 'rating':
		case 'slider':
		case 'select':
		case 'location':
			return action.type;
		default: {
			throw new Error('Unknown que type');
		}
	}
}

const initialQue = 'Single';


const AddQue = () => {
	const [que, dispatch] = useReducer(queReducer, initialQue);

	const handleCheckType = useCallback(e => {
		dispatch({
			type: e.target.value,
		});
	}, []);

	return (
		<div>
			<label htmlFor="select">{"请选择问题类型"}</label>
			{/*<select onChange={handleCheckType} name="type" id="select">*/}
			{/*	<option value="text">{'填空'}</option>*/}
			{/*	<option value="single">{'单选'}</option>*/}
			{/*	<option value="checkbox">{'多选'}</option>*/}
			{/*	<option value="rating">{'评分'}</option>*/}
			{/*	/!* 拖动滑动条选择数值 *!/*/}
			{/*	<option value="slider">{'进度条'}</option>*/}
			{/*	/!* 选项较多时, 如省份选择 *!/*/}
			{/*	<option value="select">{'下拉框'}</option>*/}
			{/*	<option value="location">{'定位'}</option>*/}
			{/*</select>*/}
			<div id='selectBox' className={select.entire}>
				<div className={select.option} id='text'>{'填空题'}</div>
				<div className={select.option} id='single'>{'单选题'}</div>
				<div className={select.option} id='checkbox'>{'多选题'}</div>
				<div className={select.option} id='rating'>{'评分题'}</div>
				<div className={select.option} id='slider'>{'进度条'}</div>
				<div className={select.option} id='location'>{'定位'}</div>
				<div className={select.option} id='select'>{'下拉框(暂不支持)'}</div>
			</div>
			{renderQue(que)}
		</div>
	);
};

export default AddQue;
