import React, {useContext} from 'react';
import {AnswersDetailsContext, QuesDetailsBoundedContext} from "../../context/renderChecked";
import involvedCheck from '../../../../style/involvedCheck/involvedCheck.module.css'

const Checkbox = ({index}) => {
	const answer = useContext(AnswersDetailsContext)[index];
	const question = useContext(QuesDetailsBoundedContext)[index];

	console.log(answer, question)


	return (
		<>
			{(!!answer && !!question) && (
				<fieldset>
					<fieldset disabled key={index} style={{border: 'none'}}>
						<legend>
							{`${index + 1}. ${question.title}`}
							<span>{'(单选题)'}</span>
						</legend>
						<div className={involvedCheck.optionsBox}>
							{question.options.map(option => (
								<div key={option.id} id={option.id} className={involvedCheck.option}>
									{/*<input type={"checkbox"} name={question.title}*/}
									{/*			 data-id={option.id}*/}
									{/*			 id={`${option.id}_checkbox_${option.content}`}*/}
									{/*			 value={option.content}*/}
									{/*			 checked={answer.answer.value.includes(option.id)}*/}
									{/*></input>*/}
									{/*<label htmlFor={`${option.id}_checkbox_${option.content}`}>{option.content}</label>*/}

									<span>{option.id}{'.'}</span>
									<span>{option.content}</span>
								</div>
							))}
						</div>
					</fieldset>
				</fieldset>
			)}
		</>
	);
};

export default Checkbox;
