// 获取用户地理位置
export function getLocation() {
	// 检查浏览器是否支持 geolocation api
	if (!navigator.geolocation) {
		return '您的浏览器不支持地理位置服务';
	}

	// 调用定位方法
	let pos = null;
	navigator.geolocation.getCurrentPosition(
		// 成功回调, 获取地理位置信息
		(position)=>{
			// console.log(position);
			pos = position;
		},
		// 失败回调, 处理错误
		(err)=>{
			console.error(err.code);
		}
	)
	return pos;
}
