import React, {useContext} from 'react';
import {AnswersDetailsContext, QuesDetailsBoundedContext} from "../../context/renderChecked";

const Single = ({index}) => {
	const answer = useContext(AnswersDetailsContext)[index];
	const question = useContext(QuesDetailsBoundedContext)[index];

	return (
		<>
			{(!!answer && !!question) && (
				<fieldset disabled key={index}>
					<legend><h3>{question.title}</h3></legend>
					{question.options.map(option => (
						<div key={option.id} id={option.id}>
							<input type={"radio"} name={question.title} id={`${option.id}_radio_${option.content}`}
										 value={option.content} checked={answer.answer.value === option.id}></input>
							<label htmlFor={`${option.id}_radio_${option.content}`}>{option.content}</label>
						</div>
					))}
				</fieldset>
			)}
		</>
	);
};

export default Single;
