import React, {useCallback, useEffect, useState} from 'react';

const Slider = ({title, max, step}) => {
	// 将用户输入的答案存到sessionStorage
	useEffect(() => {
		// 初始化sessionStorage
		sessionStorage.setItem(`${title}`, JSON.stringify({answer: 0, index}));
	}, [title])
	// 分数变化
	const [rate, setRate] = useState(step);
	const handleRateChange = useCallback(e=>{
		const newRate = e.target.value;
		setRate(newRate);
	},[title])
	// 分数存储
	useEffect(() => {
		sessionStorage.setItem(`${title}`, JSON.stringify({answer: rate, index}));
	}, [rate]);


	return (
		<fieldset>
			<legend><h3>{title}</h3></legend>
			<input type="range" onChange={handleRateChange} id={title} value={rate} min={0} max={max} step={step}/>
			<label htmlFor={title}>{rate}</label>
		</fieldset>
	);
};

export default Slider;
