	}, [allFetched]);

	return (
		<>
			{allFetched && (
				<fieldset disabled>
					<legend>{title}</legend>
					<input type="range" max={questions[index].max} step={questions[index].step} id={index}/>
					<label htmlFor={index}>{`平均进度为: `}</label>
				</fieldset>
			)}
		</>
	);
};

export default Slider;
