import React, {useCallback, useContext, useState} from 'react';
import QuesList from "../components/editSurvey/quesList";
import AddQue, {typeContext} from "../components/editSurvey/addQue";

const Publish = () => {
	const [add, setAdd] = useState(false);
	const [ques, setQues] = useState([]);
	const handleAddQue = useCallback((e) => {
		e.preventDefault();
		setAdd(true);
	}, []);
	const handleCompleteQue = useCallback(e => {
		e.preventDefault();
		const type = useContext(typeContext)
		setQues([...ques, {id:Date.now(), content:'问题'}]);
		setAdd(false);
	},[])


	return (
		<div>
			<form action="">
				<input type="text" placeholder={'请输入问卷标题'}/>
				<input type="text" placeholder={'请输入问卷描述'}/>
				<QuesList ques={ques}/>
				<div>
					<button onClick={handleAddQue}>添加问题</button>
					{add && (
						<>
							<AddQue/>
							<button onClick={handleCompleteQue}>确认添加</button>
						</>
					)}
				</div>
			</form>
		</div>
	);
};

export default Publish;
