/* 提交问卷, await直接调用
* @return bool_promise 是否提交成功
* @accept`surveyId`: 填写的问卷id */
export async function updateAnswer(surveyId) {
	console.log('开始提交');
	// 用户id
	const clientId = localStorage.getItem('token');
	// 获取sessionStorage中的问题答案信息
	const nums = sessionStorage.length;
	const answers = [];
	for (let i = 0; i < nums; i++) {
		const key = sessionStorage.key(i);
		const data = sessionStorage.getItem(key);
		answers.push({
			answer:{value: data.answer},
			questionType: data.questionType,
			index: data.index
		})
	}
	console.log(answers);
	// 更新Answer中的反馈信息
	const postResp = await fetch('http://localhost:1337/api/answers', {
		method: "POST",
		headers: {
			'Content-Type': 'application/json'
		},
		body: JSON.stringify({
			data: {
				surveyId,
				clientId,
				answers
			}
		})
	});
	if(!postResp.ok){
		alert(`${postResp.status}: 提交失败`);
		return false;
	}
	const q = await postResp.json();
	console.log(q);
	const answerId = q.data.documentId;
}
