import React, {useCallback, useContext, useRef, useState} from 'react';
import {GetTitleContext} from "../context/getTitleContext";

const Slider = () => {
	// 存储问题内容
	const [question, setQuestion] = useState('');
	// 存储最大值, 默认为100
	const [max, setMax] = useState(100);
	// 存储步长, 默认为5
	const [step, setStep] = useState(5);
	// 获取返回标题的函数
	const getTitle = useContext(GetTitleContext);
	// 每次修改标题时的上一个标题
	const lastTitle = useRef('');

	// 标题输入
	const handleTitleChange = useCallback(e => {
		const newTitle = e.target.value;
		// 清除可能存在的属于title的上一次sessionStorage
		if (lastTitle.current) {
			sessionStorage.removeItem(lastTitle.current);
		}
		lastTitle.current = newTitle;
		setQuestion(newTitle);
		// 将标题存储到sessionStorage
		const data = {
			title: newTitle,
			max,
			step,
			type: 'slider'
		};
		sessionStorage.setItem(`${newTitle}`, JSON.stringify(data));
		// 将新标题返回给publish
		getTitle(newTitle);
	}, [question, max, step]);

	// 最大值输入
	const handleMaxChange = useCallback(e => {
		const newMax = e.target.value;
		// ...sessionStorage
		const data = {
			title: question,
			max: newMax,
			step,
			type: 'slider'
		};
		sessionStorage.setItem(`${question}`, JSON.stringify(data));
	}, [question, max, step]);

	// 步长输入
	const handleStepChange = useCallback(e => {
		const newStep = e.target.value;
		// ...sessionStorage
		const data = {
			title: question,
			max,
			step: newStep,
			type: 'slider'
		};
		sessionStorage.setItem(`${question}`, JSON.stringify(data));
	}, [question, max, step])

	return (
		<div>
			<input onChange={handleTitleChange} type="text" placeholder={'请输入问题'}/><br/>
			<input onChange={handleMaxChange} type="number" placeholder={'请输入最大值(默认100)'}/><br/>
			<input onChange={handleStepChange} type="number" placeholder={'请输入滑动步长(默认5)'}/>
		</div>
	);
};

export default Slider;
