import React, {useCallback, useEffect} from 'react';

const Rating = ({title, max, step, index}) => {
	// 将用户输入的答案存到sessionStorage
	useEffect(() => {
		// 初始化sessionStorage
		sessionStorage.setItem(`${title}`, JSON.stringify({answer: 0, index}));
	}, [title])
	// 分数变化
	const handleRateChange = useCallback(e=>{
		console.log(e.target.value);
	},[title])


	return (
		<fieldset>
			<legend><h3>{title}</h3></legend>
			<input type="range" onChange={handleRateChange} min={0} max={max} step={step}/>
		</fieldset>
	);
};

export default Rating;
