import {getUserData} from "./getUserData";

/*查询用户绑定的survey
* @getDrafts Bool, 是否获取草稿, false为获取已发布*/
export async function getUserSurveys(getDrafts) {
	// 获取用户数据
	const data = await getUserData();
	// 获取草稿
	if (getDrafts) {
		const surveys = data.data.surveys.filter(survey => survey.published === false);
		const surveysDetails = [];
		for (const survey of surveys) {
			const detail = await getSurveyQues(survey.id);
			surveysDetails.push(detail);
		}
		return surveysDetails;
	}
	// 获取已发布
}

/* 根据 `surveyId` 查询相应 `survey` 的 `questions` */
async function getSurveyQues(surveyId) {
	const url = `http://localhost:1337/api/surveys/${surveyId}`;
	try {
		const resp = await fetch(url);
		if (!resp.ok) {
			throw new Error(`获取数据失败: ${resp.status}`);
		}
		const surveyDetails = await resp.json();
		return {
			title: surveyDetails.data.title,
			description: surveyDetails.data.description,
			id: surveyId,
			questions: surveyDetails.data.questions
		}
	} catch (err) {
		console.error(err);
		return null;
	}
}

/* 根据 `questionId` 获取对应的 `questionDetail` */
async function getQueDetails(questionId){
	const url = `http://localhost:1337/api/quests/${questionId}`;
	try{
		const resp = await fetch(url);
		if(!resp.ok){
			throw new Error(`获取数据失败: ${resp.status}`);
		}
		const questionDetails = await resp.json();
		const questionType = questionDetails.data.type;
		switch (questionType){
			case 'text':
			case 'location':{
				return {
					title:questionDetails.data.title,
					type: questionType
				};
			}
			case 'single':
			case 'checkbox':{
				return {
					title:questionDetails.data.title,
					type: questionType,
					
				}
			}
		}
	}catch (err){
		console.error(err);
		return null;
	}
}
