/* 用于生成在involved中排列的involved问卷组件
* @accept`surveysDetails`: 所有需要获取involved的involved信息, 数组, 其中应包含 survey.title, survey.description, answer(id) */
import {useState} from "react";

export function renderInvolvedEach(surveysDetails) {
	// 查看详情
	const checkInvolved = (e) => {
		e.preventDefault();
		console.log('查看详情')
	}

	// 控制详情按钮的显示
	const [hoveringId, setHoveringId] = useState(null);
	const checkHoveringFocusing = (e) => {
		setHoveringId(e.currentTarget.id);
	}
	const checkHoveringLosing = (e) => {
		setHoveringId(null);
	}


	const elements = [];
	surveysDetails.forEach(surveyDetails => {
		elements.push(
			<fieldset key={surveyDetails.answer} id={surveyDetails.answer} onMouseEnter={checkHoveringFocusing}
								onMouseLeave={checkHoveringLosing}>
				<h1>{surveyDetails.survey.title}</h1>
				<h2>{surveyDetails.survey.description}</h2>
				<hr/>
				{hoveringId === surveyDetails.answer && <button onClick={checkInvolved}>{'查看详情'}</button>}
			</fieldset>
		);
	});
	return elements;
}
