import React, {useCallback, useEffect, useState} from 'react';

const Single = () => {
	// 存储问题内容
	const [question, setQuestion] = useState('');
	// 存储选项列表, [{id, content}]
	const [options, setOptions] = useState([]);
	// 当前选项编号, 1 -> A
	const [optionIndex, setOptionIndex] = useState(1);

	// 问题输入
	const handleQuestionChange = useCallback(e => {
		const quesTitle = e.target.value;
		setQuestion(quesTitle);
		// ...sessionStorage
		sessionStorage.setItem(`${question}__options`, JOSN.stringify({title: quesTitle, options: options}));
	}, [options]);

	// 添加新选项
	const addOption = useCallback(e => {
		e.preventDefault();
		// 选项标识 A,B,C,...
		const label = String.fromCharCode(optionIndex + 64);
		// 选项编号递增, 用于下次生成新标识, 函数式更新避免依赖optionIndex
		setOptionIndex(prevIndex => prevIndex+1);
		// 	添加新选项到数组, 初始为空
		const newOptions = [...options, {id: label, content: ''}];
		setOptions(newOptions);
		// ...sessionStorage
		sessionStorage.setItem(`${question}__options`, JSON.stringify({title: question, options: newOptions}));
	}, [question]);

	// 选项内容变化
	const handleOptionChange = useCallback((e) => {
		// 更新对应选项内容
		console.log(e.target.value)
		const id = e.target.id;
		// 若选项id相同则更新content
		const newOptions = options.map(option => option.id === id ? {...option, content: e.target.value} : option);
		setOptions(newOptions);
		// ...sessionStorage
		sessionStorage.setItem(`${question}__options`, JSON.stringify({title: question, options: newOptions}));
	}, [question]);


	useEffect(() => {
		console.log(options);
	})


	return (
		<div>
			<input onChange={handleQuestionChange} placeholder={'请输入问题'}/>
			{/* 选项列表 */}
			<div>
				{options.map(
					(option, index) => (
						<div key={option.id}>
							<span>{option.id}</span>
							<input type="text" id={option.id} onChange={handleOptionChange} placeholder={'请输入内容'}/>
						</div>
					))}
			</div>
			<button onClick={addOption}>添加选项</button>
		</div>
	);
};

export default Single;
