	return (
		<fieldset disabled onMouseEnter={handleEnter} onMouseLeave={handleLeave}>
			<legend>{que}</legend>
			{options.map(option => (
				<div key={key} id={option.id}>
					<input type={"radio"} value={option.content}></input>
					<label htmlFor={option.content}>{option.content}</label>
				</div>
			))}
			{isHover && (
				<>
					<button onClick={handleEdit}>{'编辑'}</button>
					<button onClick={handleDel}>{'删除'}</button>
				</>
			)}
		</fieldset>
	);
};

export default Single;
