import React, {useCallback, useEffect, useState} from 'react';
import {getLocation} from "../../../utils/getLocation";

const Location = ({key, que}) => {
	const [position, setPosition] = useState(null);
	const [isText, setIsText] = useState(true);

	// 是否自己输入位置信息
	const handleChoose = useCallback(e => {
		switch (e.target.value){
			case 'text':{
				setIsText(true);
				return;
			}
			case 'location':{
				setIsText(false);
				return;
			}
		}
	}, [isText, position]);

	// 精确定位
	const handleGetPosition = useCallback(e => {
		e.preventDefault();
		const po = getLocation();
		setPosition(po);
	},[isText, position]);

	useEffect(()=>{
		console.log(position)
	},[position])

	return (
		<fieldset>
			<legend>{que}</legend>
			<select onChange={handleChoose} name="location" id="">
				<option value="text">自己输入</option>
				<option value="location">定位精确位置信息</option>
			</select>
			{isText ? (
				<input type="text" placeholder={'请输入位置信息'}/>
			):(
				<div>
					<span>{position}</span>
					<button onClick={handleGetPosition}>定位</button>
				</div>
			)}
		</fieldset>
	);
};

export default Location;
