import React, {useContext, useEffect, useState} from 'react';
import {AnswersInfosContext, QuesBaseInfosContext} from "../../context/visualizationContext";
import involvedCheck from '../../../../style/involvedCheck/involvedCheck.module.css'

const Slider = ({title, index}) => {
	console.log('>>>>>>>>>>>>>>>');
	const questions = useContext(QuesBaseInfosContext);
	const answers = useContext(AnswersInfosContext);
	const [allFetched, setAllFetched] = useState(false);
	useEffect(() => {
		if (questions !== null && answers !== null) {
			setAllFetched(true);
		}
	}, [questions, answers]);


	const [average, setAverage] = useState(0);
	useEffect(() => {
		if (answers !== null) {
			let total = 0;
			for (let answer of answers) {
				total += answer[index].answer.value;
			}
			setAverage(total / answers.length);
		}
		console.log('????')
	}, [allFetched]);

	return (
		<>
			<fieldset disabled>
				<legend>{title}</legend>
				<input type="range" max={questions[index].max} step={questions[index].step} value={average} id={index}
							 disabled={true}/>
				<label htmlFor={index}>{`平均进度为: ${average}`}</label>
			</fieldset>
		</>
	);
};

export default Slider;
