import React from 'react';
import {getDisplayElements} from "./quesPreview/getDisplayElements";
import {filterQueDetails} from "./filterQueDetails";
import {renderQue} from "./renderQue";

const QuesList = ({ques, editingQue, isEditing}) => {
	let type;
	const quesFront = [];
	const quesBehind = [];
	let index = null;
	if(isEditing){
		type = JSON.parse(sessionStorage.getItem(editingQue)).type;
		ques.forEach((que, ind) => {
			if (que.title === editingQue) {
				index = ind;
			}
		});
		ques.forEach((que, ind) => {
			if (index === 0) {
				if (que.title !== que) {
					quesBehind.push(que);
				}
			} else if (index === ques.length) {
				if (que.title !== que) {
					quesFront.push(que);
				}
			} else {
				if (que.title !== que && ind < index) {
					quesFront.push(que);
				} else if (que.title !== que && ind > index) {
					quesBehind.push(que);
				}
			}
		})
	}



	const elements = getDisplayElements(ques);

	return (
		<div>
			{!isEditing && elements}
			{isEditing && (
				<>
					{getDisplayElements(quesFront)}
					{renderQue(type)}
					{getDisplayElements(quesBehind)}
				</>
			)}
		</div>
	);
};

export default QuesList;
