import React, {useCallback, useState} from 'react';

const Checkbox = ({title, options, index}) => {
	// 将用户选择的答案存到sessionStorage
	const [checkedOptions, setCheckedOptions] = useState([]);
	sessionStorage.setItem(`${title}`, JSON.stringify({answer: [], index}));
	const checkboxChoose = useCallback(e => {
		// 当前点击的checkbox
		const touchedOption = e.currentTarget.id;
		const isChcked = e.target.checked; // 是否被选中
		// 目前所有被选中的checkbox(未更新)
		const options = checkedOptions;
		// 查找是否包含点击的checkbox
		const ind = options.findIndex((touchedOption, indexx) => indexx);
		// 不包含则添加, 包含则移除
		if (ind === -1 && isChcked === true) options.push(touchedOption);
		if (!!ind && isChcked === false) options.splice(ind, 1);
		console.log(ind, touchedOption);
		setCheckedOptions(options);
		sessionStorage.setItem(`${title}`, JSON.stringify({answer: options, index}));
	}, [title]);

	return (
		<fieldset>
			<legend><h3>{title}</h3></legend>
			{options.map(option => (
				<div key={option.id} id={option.id} onClick={checkboxChoose}>
					<input type={"checkbox"} name={title} id={`${option.id}_checkbox_${option.content}`}
								 value={option.content}></input>
					<label htmlFor={`${option.id}_checkbox_${option.content}`}>{option.content}</label>
				</div>
			))}
		</fieldset>
	);
};

export default Checkbox;
