import React, {useCallback, useEffect, useState} from 'react';
import {useRegisterUserMutation, useLoginUserMutation, useFindUserQuery, userApi} from "../services/apis";
import {useLogin} from '../utils/login'
import {register} from "../utils/register";


const Login = () => {
	const [entired, setEntired] = useState(false); // 是否全部填写
	const [user, setUser] = useState(''); // 监测 user 输入框
	const [pwd, setPWD] = useState(''); // 监测 pwd 输入框

	// 使用 useCallback, 确保监测函数只在挂载时创建一次
	const handleInputUser = useCallback((e) => {
		// user 监听器
		setUser(e.target.value);
	}, []);
	const handleInputPWD = useCallback((e) => {
		// 	pwd 监听器
		setPWD(e.target.value);
	}, []);
	const handleEntiredCheck = useCallback((user, pwd) => {
		// 检查是否全部填写
		const temp = (!!user && !!pwd);
		setEntired(temp);
		// return temp;
	}, [])

	// 注册处理
	const handleRegister = (user, pwd) => {
		handleEntiredCheck(user, pwd);
		if(entired){
			const resp = register(user, pwd);
		}else {
			console.log('用户密码不完整');
		}
	}

	useEffect(() => {
	}, [user, pwd, handleEntiredCheck]);

	return (
		<div>
			<form action="">
				<input type="text" value={user} placeholder="用户名"/>
				<input type="text" value={pwd} placeholder="请输入密码"/>
				<button type="submit">登录</button>
				<button type="submit">注册</button>
			</form>
		</div>
	);
};

export default Login;
