import React, {useCallback, useEffect, useState} from 'react';

const Checkbox = ({title, options, index}) => {
	// 将用户选择的答案存到sessionStorage
	const [checkedOptions, setCheckedOptions] = useState([]);
	useEffect(() => {
		sessionStorage.setItem(`${title}`, JSON.stringify({answer: [], index}));
	}, [title])
	const checkboxChoose = useCallback(e => {
		// 当前点击的checkbox
		console.log(e.target.dataset.id);
		const touchedOption = e.currentTarget.id;
		const checked = e.target.checked; // 是否被选中
		// 查找是否包含点击的checkbox
		// 不包含则添加, 包含则移除
		setCheckedOptions(prevState => {
			if (checked) {
				return prevState.includes(touchedOption) ? prevState : [...prevState, touchedOption];
			} else {
				return prevState.filter(option => option !== touchedOption);
			}
		})
	}, [title]);
	useEffect(() => {
		sessionStorage.setItem(`${title}`, JSON.stringify({answer: checkedOptions, index}));
	}, [checkedOptions])

	return (
		<fieldset>
			<legend><h3>{title}</h3></legend>
			{options.map(option => (
				<div key={option.id} id={option.id}>
					<input type={"checkbox"} onClick={checkboxChoose} name={title}
								 data-id={option.id}
								 id={`${option.id}_checkbox_${option.content}`}
								 value={option.content}></input>
					<label htmlFor={`${option.id}_checkbox_${option.content}`}>{option.content}</label>
				</div>
			))}
		</fieldset>
	);
};

export default Checkbox;
